#!/bin/bash
# Here you can create functions which will be available from the commands file
# You can also use here user variables defined in your config file
motiondetectiondlink_cmddisable='curl "http://'"$motiondetection_camip"'/setSystemMotion" -H "Host: '"$motiondetection_camip"'" -H "Referer: http://'"$motiondetection_camip"'/setSystemMotion" -H "Authorization: Basic '"$motiondetection_camauthcode"'" -H "Connection: keep-alive" -H "Upgrade-Insecure-Requests: 1" --data "ReplySuccessPage=motion.htm&ReplyErrorPage=motion.htm&MotionDetectionEnable=0&MotionDetectionScheduleDay=127&ConfigSystemMotion=Save" >/dev/null 2>&1'
motiondetectiondlink_cmdenable='curl "http://'"$motiondetectiondlink_camip"'/setSystemMotion" -H "Host: '"$motiondetectiondlink_camip"'" -H "Referer: http://'"$motiondetectiondlink_camip"'/setSystemMotion" -H "Authorization: Basic '"$motiondetectiondlink_camauthcode"'" -H "Connection: keep-alive" -H "Upgrade-Insecure-Requests: 1" --data "ReplySuccessPage=motion.htm&ReplyErrorPage=motion.htm&MotionDetectionEnable=1&MotionDetectionScheduleDay=127&MotionDetectionScheduleMode=0&MotionDetectionSensitivity=70&ConfigSystemMotion=Save" >/dev/null 2>&1'
